class.script:
  // 
var WebSocket = require('webSocket');
var opened = false;
var dataQueue = [];
var dataQueueIsLocked = false;
function sendDataInQueue() {
  while (dataQueueIsLocked);
  dataQueueIsLocked = true;
  if (opened) {
    var queueLength = dataQueue.length;
    for (var i = 0; i < queueLength; i++) {
      ws.send(dataQueue[i]);
    }
    dataQueue = [];
  }
  dataQueueIsLocked = false;
}
function onOpen() {
  print('client: connection to a server established');
  opened = true;
  send(1, status);
  sendDataInQueue();
}
function onClose(message) {
  print('client: connection is closed. ' + message);
  openend = false;
  send(0, status);
}
function onError() {
  print('client: an error occurred.');
  openend = false;
  send(-1, status);
}
function onMessage(received, flags) {
  print('client: onMessage');
  if (flags.binary) {
    received = WebSocket.binToStr(received);
  }
  send(received, message);
}
var ws = null;
var inputQueue = [];
function inputHandler() {
  inputQueue.push(get(data));
}
function initialize() {
  inputQueue = [];
  addInputHandler(data, inputHandler);
  if (ws == null) {
    print('client: connecting to a server');
    ws = new WebSocket.Socket(get(url));
    ws.on('open', onOpen);
    ws.on('message', onMessage);
    ws.on('close', onClose);
    ws.on('error', onError);
  }
}
function wrapup() {
  if (ws != null) {
    ws.close();
    print('client: closing the socket');
  }
  opened = false;
}
function fire() {
  if (get(enable)) {
    var queueLength = inputQueue.length;
    for (var i = 0; i < queueLength; i++) {
      dataQueue.push(inputQueue[i]);
    }
    inputQueue = [];
  }
  sendDataInQueue();
}
